course["CIS 161"] = {"name":"Computer Science 1", "cred":int(4), "dept":"CIS", "desc":"Examines the nature of computer programming; includes discussion of a computer model, methods of problem solving and programming structures; information representation; algorithm construction; object-oriented design."}
