course["MUS 118"] = {"name":"Music Technology MIDI/Audio", "cred":int(3), "dept":"MUS", "desc":"Provides hands-on instruction in current applications of music technology in a MIDI/audio studio. Use various music production tools in live sound engineering and mixing."}
